var mongoose = require("mongoose");
var campGroundSchema = new mongoose.Schema({
  name: String,
  price: Number,
  image: String,
  description: String,
  author: {
    id: {
      type: mongoose.Schema.Types.ObjectID,
      ref: "User"
    },
    username: String
  },
  comments : [
    {
      type:mongoose.Schema.Types.ObjectID,
      ref: "Comment"
    }
  ]
});
module.exports = mongoose.model("campgroundDB", campGroundSchema);